delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `get_school_count`(_name VARCHAR(40), _type INT)
    READS SQL DATA
BEGIN
    IF _type=1 THEN
        SELECT COUNT(school_name) FROM school
        WHERE school_name=_name;
    ELSEIF _type=2 THEN
        SELECT COUNT(school_url) FROM school
        WHERE school_url=_name;
    END IF;
END
 ;;
