CREATE DEFINER=`root`@`localhost` PROCEDURE `get_school_list`(_start numeric,_size numeric)
    READS SQL DATA
BEGIN
	set @pageStart = _start;
    set @pageSize = _size;
	set @sql = concat('select * from school limit ?,?');
	prepare stmt from @sql;
	execute stmt USING @pageStart, @pageSize;
	DEALLOCATE PREPARE stmt;
END
 ;;
