delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `update_school_status`(_username varchar(40),_url varchar(40),_type varchar(1))
BEGIN
	DECLARE t_error INTEGER DEFAULT 0;
    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;
	START TRANSACTION;
		update school set _status = _type where username = _username and school_url = _url;
        update account set _status = _type where username = _username;
	IF t_error=1 THEN
        ROLLBACK;
        SELECT '-1' AS success;
    ELSE
        COMMIT;
        SELECT '1' AS success;
    END IF;
END
 ;;
delimiter ;

-- ----------------------------
--  Function structure for `currval`
-- ----------------------------
DROP FUNCTION IF EXISTS `currval`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `currval`(v_seq_name VARCHAR(40)) RETURNS int(11)
begin
    declare _value integer;
    set _value = 0;
    select current_val into _value
    from sequence
    where seq_name = v_seq_name;
    return _value;
end
 ;;
delimiter ;

-- ----------------------------
--  Function structure for `nextval`
-- ----------------------------
DROP FUNCTION IF EXISTS `nextval`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `nextval`(v_seq_name VARCHAR(40)) RETURNS int(11)
begin
  update sequence
  set current_val = current_val + increment_val
  where seq_name = v_seq_name;
  return currval(v_seq_name);
end
 ;;
delimiter ;

