mapper.update:
UPDATE MID_TEAM_COM
SET TEAM_COM_STATUS = 1
WHERE
	MID_TEAM_COM.TEAM_ID = #{team_id}
AND MID_TEAM_COM.COM_ID =#{match_id}

UPDATE MID_TEAM_COM
SET TEAM_COM_STATUS = 2
WHERE
	MID_TEAM_COM.TEAM_ID = #{team_id}
AND MID_TEAM_COM.COM_ID = #{match_id}

UPDATE TEAM
SET ORG_ID = #{org_id}
WHERE
	TEAM_ID = #{team_id}
